        [Fact]
        public void ToUnit()
        {
            var kelvinperkilopascal = JouleThomsonCoefficient.FromKelvinsPerKilopascal(1);

            var degreefahrenheitperpoundsforcepersquareinchQuantity = kelvinperkilopascal.ToUnit(JouleThomsonCoefficientUnit.DegreeFahrenheitPerPoundsForcePerSquareInch);
            AssertEx.EqualTolerance(DegreesFahrenheitPerPoundsForcePerSquareInchInOneKelvinPerKilopascal, (double)degreefahrenheitperpoundsforcepersquareinchQuantity.Value, DegreesFahrenheitPerPoundsForcePerSquareInchTolerance);
            Assert.Equal(JouleThomsonCoefficientUnit.DegreeFahrenheitPerPoundsForcePerSquareInch, degreefahrenheitperpoundsforcepersquareinchQuantity.Unit);

            var kelvinperbarQuantity = kelvinperkilopascal.ToUnit(JouleThomsonCoefficientUnit.KelvinPerBar);
            AssertEx.EqualTolerance(KelvinsPerBarInOneKelvinPerKilopascal, (double)kelvinperbarQuantity.Value, KelvinsPerBarTolerance);
            Assert.Equal(JouleThomsonCoefficientUnit.KelvinPerBar, kelvinperbarQuantity.Unit);

            var kelvinperkilopascalQuantity = kelvinperkilopascal.ToUnit(JouleThomsonCoefficientUnit.KelvinPerKilopascal);
            AssertEx.EqualTolerance(KelvinsPerKilopascalInOneKelvinPerKilopascal, (double)kelvinperkilopascalQuantity.Value, KelvinsPerKilopascalTolerance);
            Assert.Equal(JouleThomsonCoefficientUnit.KelvinPerKilopascal, kelvinperkilopascalQuantity.Unit);
        }

