        [Fact]
        public void ArithmeticOperators()
        {
            JouleThomsonCoefficient v = JouleThomsonCoefficient.FromKelvinsPerKilopascal(1);
            AssertEx.EqualTolerance(-1, -v.KelvinsPerKilopascal, KelvinsPerKilopascalTolerance);
            AssertEx.EqualTolerance(2, (JouleThomsonCoefficient.FromKelvinsPerKilopascal(3)-v).KelvinsPerKilopascal, KelvinsPerKilopascalTolerance);
            AssertEx.EqualTolerance(2, (v + v).KelvinsPerKilopascal, KelvinsPerKilopascalTolerance);
            AssertEx.EqualTolerance(10, (v*10).KelvinsPerKilopascal, KelvinsPerKilopascalTolerance);
            AssertEx.EqualTolerance(10, (10*v).KelvinsPerKilopascal, KelvinsPerKilopascalTolerance);
            AssertEx.EqualTolerance(2, (JouleThomsonCoefficient.FromKelvinsPerKilopascal(10)/5).KelvinsPerKilopascal, KelvinsPerKilopascalTolerance);
            AssertEx.EqualTolerance(2, JouleThomsonCoefficient.FromKelvinsPerKilopascal(10)/JouleThomsonCoefficient.FromKelvinsPerKilopascal(5), KelvinsPerKilopascalTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            JouleThomsonCoefficient oneKelvinPerKilopascal = JouleThomsonCoefficient.FromKelvinsPerKilopascal(1);
            JouleThomsonCoefficient twoKelvinsPerKilopascal = JouleThomsonCoefficient.FromKelvinsPerKilopascal(2);

            Assert.True(oneKelvinPerKilopascal < twoKelvinsPerKilopascal);
            Assert.True(oneKelvinPerKilopascal <= twoKelvinsPerKilopascal);
            Assert.True(twoKelvinsPerKilopascal > oneKelvinPerKilopascal);
            Assert.True(twoKelvinsPerKilopascal >= oneKelvinPerKilopascal);

            Assert.False(oneKelvinPerKilopascal > twoKelvinsPerKilopascal);
            Assert.False(oneKelvinPerKilopascal >= twoKelvinsPerKilopascal);
            Assert.False(twoKelvinsPerKilopascal < oneKelvinPerKilopascal);
            Assert.False(twoKelvinsPerKilopascal <= oneKelvinPerKilopascal);
        }

