            Thread.CurrentThread.CurrentUICulture = CultureInfo.GetCultureInfo("en-US");
            try {
                Assert.Equal("1 °F/psi", new JouleThomsonCoefficient(1, JouleThomsonCoefficientUnit.DegreeFahrenheitPerPoundsForcePerSquareInch).ToString());
                Assert.Equal("1 K/bar", new JouleThomsonCoefficient(1, JouleThomsonCoefficientUnit.KelvinPerBar).ToString());
                Assert.Equal("1 K/kPa", new JouleThomsonCoefficient(1, JouleThomsonCoefficientUnit.KelvinPerKilopascal).ToString());
            }
            finally
            {
                Thread.CurrentThread.CurrentUICulture = prevCulture;
            }
        }

        [Fact]
        public void ToString_WithSwedishCulture_ReturnsUnitAbbreviationForEnglishCultureSinceThereAreNoMappings()
        {
            // Chose this culture, because we don't currently have any abbreviations mapped for that culture and we expect the en-US to be used as fallback.
            var swedishCulture = CultureInfo.GetCultureInfo("sv-SE");

            Assert.Equal("1 °F/psi", new JouleThomsonCoefficient(1, JouleThomsonCoefficientUnit.DegreeFahrenheitPerPoundsForcePerSquareInch).ToString(swedishCulture));
            Assert.Equal("1 K/bar", new JouleThomsonCoefficient(1, JouleThomsonCoefficientUnit.KelvinPerBar).ToString(swedishCulture));
