        }

        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 K/kPa", new JouleThomsonCoefficient(0.123456, JouleThomsonCoefficientUnit.KelvinPerKilopascal).ToString("s1"));
                Assert.Equal("0.12 K/kPa", new JouleThomsonCoefficient(0.123456, JouleThomsonCoefficientUnit.KelvinPerKilopascal).ToString("s2"));
                Assert.Equal("0.123 K/kPa", new JouleThomsonCoefficient(0.123456, JouleThomsonCoefficientUnit.KelvinPerKilopascal).ToString("s3"));
                Assert.Equal("0.1235 K/kPa", new JouleThomsonCoefficient(0.123456, JouleThomsonCoefficientUnit.KelvinPerKilopascal).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

